import React,{useState, useEffect} from 'react';
import axios from 'axios';
import IdeaCRUD from '../../componentes/idea/IdeaCRUD';

const MisIdeas = () => {
    const [ideas,setIdeas] = useState('');
    const userID = localStorage.getItem('user');
    useEffect(()=>{
        axios.get(`http://localhost:5000/v1/api/idea/${userID}/all`)
        .then((respuesta)=>{
            console.log(respuesta.data);
            setIdeas(respuesta.data);
        })
        .catch(console.log)
    },[!ideas])
  
  return (
    <div className='container'>
        <div className="d-flex align-items-center mt-5 justify-content-center">
        <button className='btn btn-success text-center bold' style={{width: '200px'}}>Crear nueva idea</button>

        </div>

        {ideas && ideas.map((idea)=>{return <IdeaCRUD datos={idea} key={ideas._id}/>})}

    </div>
  )
}

export default MisIdeas
