import React, { useState } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import BtnSesion from '../alertas/btn_alertas/BtnSesion';

const Idea = ({ datos }) => {
  const token = document.cookie.replace('token=', '');

  const [idea, setIdea] = useState({
    id: datos._id,
    titulo: datos.idea,
    descripcion: datos.description,
    autor: datos.author.name,
    votos: datos.upvotes.length - datos.downvotes.length,
    comentarios: datos.comments.length

  });
  function cantVotos(datosDeVotos) {
    return datosDeVotos.upvotes.length - datosDeVotos.downvotes.length;
  }
  const downVote = e => {
    e.preventDefault();
    axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/downvote`,{}, {
      headers: {
        'authorization': token
      }
    })
      .then(dat => {
        console.log(dat);
        setIdea({ ...idea, votos: cantVotos(dat.data) })
      })
      .catch(console.log)

  }

  const upVote = e => {
    e.preventDefault();
    axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/upvote`
    ,{}, {
      headers: {
        'authorization': token
      }}
    )
      .then(dat => {
        console.log(dat);
        setIdea({ ...idea, votos: cantVotos(dat.data) })
      })
      .catch(()=>{
        document.cookie = "token=; max-age=0";
        localStorage.removeItem('user');
              window.location = '/';
      })

  }


  return (
    <>
      <Card
        bg={'light'}
        key={idea.id}
        text={'dark'}
        className="mb-2 mt-5 shadow"
      >
        <Card.Header className='fw-bold'>{idea.titulo}</Card.Header>
        <Card.Body>
          <Card.Text>
            {idea.descripcion}
          </Card.Text>
          <footer className="blockquote-footer">
            Author: <cite title="Source Title">{idea.autor}</cite>
          </footer>
         
        </Card.Body>
        <Card.Footer >
          <div className='d-flex justify-content-around'>
          <h2>Puntos: {datos.upvotes.length - datos.downvotes.length}</h2>
          {token?
         <form onSubmit={upVote}>
         <input type="hidden" name="up" value={datos._id} />
         <Button className='btn-success'type="submit">positivo</Button>
       </form>:
          <form>
          <BtnSesion boton='success' textoBtn='Positivo'></BtnSesion>
          </form>
        
        
        }
          {token?
            <form onSubmit={downVote}>
            <input type="hidden" name="down" value={datos._id} />
            <Button className="btn-danger" type="submit">negativo</Button>
          </form>:
          <form>
          <BtnSesion boton='danger' textoBtn='negativo'></BtnSesion>
          </form>
        
        
        }
        
          </div>
          <hr />
     <Link to={`/idea/${idea.id}`}className='text-center text-primary text-decoration-none'><h3 >Comentarios{`(${idea.comentarios})`}</h3></Link>
        </Card.Footer>
      </Card>
    </>

  )
}

export default Idea
