import React, { useState , useEffect} from 'react';
import './style.css'
import axios from 'axios';
import { Card } from 'react-bootstrap';
import { Link} from 'react-router-dom';
import SectionBtn from '../alerts/btnAlerts/SectionBtn';
const Idea = ({ data }) => {
  const token = document.cookie.replace('token=', '');
  const userLogged = JSON.parse(localStorage.getItem('user'));
  const idea={
    id: data._id,
    title: data.idea,
    description: data.description,
    author: data.author.name,
    comments: data.comments.length
  };
  const voteScore = ()=>{
    if(data.vote.length > 0){
        const positive = data.vote.filter(el => el.vote === true);
        const negative = data.vote.filter(el => el.vote === false);
        return positive.length - negative.length;
      }
  }
   
  const [totalVotes, setTotalVotes] = useState(voteScore)

  const [voted,setVoted] = useState('');
 const [idVote, setIdVote] = useState('');
  const allVotes = data.vote;


 useEffect(()=>{
  if(allVotes.length>0){
    for(let i = 0; i < allVotes.length ; i++){
      if(allVotes[i].author._id === userLogged){
        setIdVote(allVotes[i]._id);
        if(allVotes[i].vote === true){
          setVoted(true);
        }else{
          setVoted(false);
        }
  
      }  
    }
  }
 },[]);






  const downVote = e => {
    e.preventDefault();
    if (voted === false){
      axios.delete(`http://localhost:5000/v1/api/vote/${idVote}`, {
        headers: {
          'authorization': token
        }
      })
        .then(dat => {
          setVoted('');
          setTotalVotes(totalVotes - 1);
        })
        .catch(console.log)
    }else if(voted === true){
      axios.patch(`http://localhost:5000/v1/api/vote/${idVote}`,{
      vote: false
    }, {
      headers: {
        'authorization': token
      }
    })
      .then(dat => {
        setVoted(false);
      })
      .catch(console.log)
    }else{
      axios.post(`http://localhost:5000/v1/api/vote/${data._id}`,{
        vote: false
      }, {
        headers: {
          'authorization': token
        }
      })
        .then(dat => {
          setVoted(false);
          setTotalVotes(totalVotes - 1);
        })
        .catch(console.log)
    }
   
  }

  const upVote = e => {
    e.preventDefault();
    if (voted === true){
      axios.delete(`http://localhost:5000/v1/api/vote/${idVote}`, {
        headers: {
          'authorization': token
        }
      })
        .then(dat => {
          setVoted('');
          setTotalVotes(totalVotes - 1);
        })
        .catch(console.log)
    }else if(voted === false){
      axios.patch(`http://localhost:5000/v1/api/vote/${idVote}`,{
      vote: true
    }, {
      headers: {
        'authorization': token
      }
    })
      .then(dat => {
        setVoted(true);
      })
      .catch(console.log)
    }else{
    axios.post(`http://localhost:5000/v1/api/vote/${data._id}`
    ,{
      vote: true
    }, {
      headers: {
        'authorization': token
      }}
    )
      .then(dat => {
        setVoted(true);
        setTotalVotes(totalVotes + 1);
      })
      .catch(console.log)
    }

  }

  
  
  const upclass = ()=>{
    if(voted === true){
      return 'votedup'
    }else{
      return 'upvote'
    }
   
   
    }

const downclass = ()=>{
  if(voted === false){
    return 'voteddown'
  }else{
    return 'downvote'
  }
}
  
  return (
    <>
      <Card
        bg={'transparent'}
        key={idea.id}
        text={'dark'}
        className=" mt-5 shadow"
      >
        <Card.Header className='fw-bold'>{idea.title}</Card.Header>
        <Card.Body>
          <Card.Text>
            {idea.description}
          </Card.Text>
          <footer className="blockquote-footer">
            Author: <cite title="Source Title">{idea.author}</cite>
          </footer>
         
        </Card.Body>
        <Card.Footer >
          <div className='d-flex justify-content-around'>
          <h2>Score: {totalVotes}</h2>
          {token?
         <form onSubmit={upVote}>
         <input type="hidden" name="up" value={data._id} />
         <button className={upclass()}type="submit"></button>
       </form>:
          <form>
          <SectionBtn type='upvote'></SectionBtn>
          </form>
        
        
        }
          {token?
            <form onSubmit={downVote}>
            <input type="hidden" name="down" value={data._id} />
            <button className={downclass()}type="submit"></button>
          </form>:
          <form>
          <SectionBtn type='downvote'></SectionBtn>
          </form>
        
        
        }
        
          </div>
          <hr />
     <Link to={`/idea/${idea.id}`}className='text-center  text-decoration-none'style={{color:'#2b86c5'}} ><h3 >comments{`(${idea.comments})`}</h3></Link>
        </Card.Footer>
      </Card>
    </>

  )
}

export default Idea
