import React, { useEffect } from 'react';

import { useIdea, IdeaProvider } from '../../context/idea.context'
import Idea from '../../componentes/idea/Idea';

export default () => <IdeaProvider>
  <TopIdeas></TopIdeas>
</IdeaProvider>
const TopIdeas = () => {
  const { getIdeas,
    ideas
  } = useIdea();

  useEffect(() => {
    getIdeas();
  }, [])

  let masVotados = ideas.filter(vote => { return vote.upvotes.length - vote.downvotes.length > 0 }).sort((a, b) => {
    const votosA = a.upvotes.length - a.downvotes.length;
    const votosB = b.upvotes.length - b.downvotes.length;
    return votosB - votosA;
  })
  let masComentados = ideas.filter(vote => { return vote.comments.length > 0 }).sort((a, b) => {
    const commentsA = a.comments.length;
    const commentsB = b.comments.length;
    return commentsB - commentsA;
  })
  masComentados.length = 10;
  masVotados.length = 10;


  return (
    <div className='container pb-5'>


      <h1 className='text-center titulo text-uppercase mt-5'>Top 10 ideas más votadas</h1>

      {
        masVotados.length > 0 &&
        masVotados.map((idea, index) =>
          <div >
            <h2 className='top mt-5 btn btn-dark fw-bold text-uppercase ' >{`Puesto N°${index + 1}`}</h2>
            <a href={`/idea/${idea._id}`} key={idea._id} style={{ textDecoration: 'none' }}>


              <Idea datos={idea} key={idea._id} />
            </a>
          </div>

        )
      }
      <h1 className='text-center titulo text-uppercase mt-5'>Top 10 ideas más Comentadas</h1>

      {
        masComentados.length > 0 &&
        masComentados.map((idea, index) =>
          <div >
            <h2 className='top mt-5 btn btn-warning fw-bold text-uppercase ' >{`Puesto N°${index + 1}`}</h2>
            <a href={`/idea/${idea._id}`} key={idea._id} style={{ textDecoration: 'none' }}>
              <Idea datos={idea} key={idea._id} />
            </a>
          </div>
        )
      }

    </div>
  )
}
