import React , {useState}from 'react';
import axios from 'axios';
import Idea from './Idea';

const IniciarApp = () => {
    const [copia, setCopia]  = useState('null');
      let datos;
      axios.get('http://localhost:5000/v1/api/idea')
      .then(dat =>{
          datos = dat.data;
          setCopia(datos);
      console.log(datos);
      
      })
      .catch(console.log)    
  
  
  return (
    <div>
      
{datos.length > 0&& datos.map(idea => <Idea datos={idea}/>)}
{!copia&&<p>error!!</p>}

   </div>
  )
}

export default IniciarApp
