import React, { useState , useEffect} from 'react';
import './style.css'
import axios from 'axios';
import { Card } from 'react-bootstrap';
import { Link} from 'react-router-dom';
import BtnSesion from '../alertas/btn_alertas/BtnSesion';

const Idea = ({ datos }) => {
  const token = document.cookie.replace('token=', '');
  const userLogged = JSON.parse(localStorage.getItem('user'));
  const [idea, setIdea] = useState({
    id: datos._id,
    titulo: datos.idea,
    descripcion: datos.description,
    autor: datos.author.name,
    votos: datos.vote.length,
    comentarios: datos.comments.length
  });

 const [votado,setVotado] = useState('');
const [idVoto, setIdVoto] = useState('');
 const votosnashe = datos.vote;
 useEffect(()=>{

  if(votosnashe.length>0){

    for(let i = 0; i < votosnashe.length ; i++){
  if(votosnashe[i].author._id === userLogged){
    setIdVoto(votosnashe[i]._id);
    if(votosnashe[i].vote === true){
      setVotado(true);
    }else{
      setVotado(false);
    }
  
  }  }
   }
 },[]);





  function cantVotos(datosDeVotos) {
    return datosDeVotos;
  }
  const downVote = e => {
    e.preventDefault();
    if (votado === false){
      axios.delete(`http://localhost:5000/v1/api/vote/${idVoto}`, {
        headers: {
          'authorization': token
        }
      })
        .then(dat => {
          console.log(dat);
          setVotado('');
          setIdea({ ...idea, votos: votos-1  })
        })
        .catch(console.log)
    }else if(votado === true){
      axios.patch(`http://localhost:5000/v1/api/vote/${idVoto}`,{
      vote: false
    }, {
      headers: {
        'authorization': token
      }
    })
      .then(dat => {
        setVotado(false);
        console.log(dat);

        setIdea({ ...idea, votos: votos-1})
      })
      .catch(console.log)
    }else{
      axios.post(`http://localhost:5000/v1/api/vote/${datos._id}`,{
        vote: false
      }, {
        headers: {
          'authorization': token
        }
      })
        .then(dat => {
          setVotado(false);
          console.log(dat);

          setIdea({ ...idea, votos: cantVotos(dat.data) })
        })
        .catch(console.log)
    }
   
  }

  const upVote = e => {
    e.preventDefault();
    if (votado === true){
      axios.delete(`http://localhost:5000/v1/api/vote/${idVoto}`, {
        headers: {
          'authorization': token
        }
      })
        .then(dat => {
          console.log(dat);
          setVotado('');

          setIdea({ ...idea, votos: cantVotos(dat.data) })
        })
        .catch(console.log)
    }else if(votado === false){
      axios.patch(`http://localhost:5000/v1/api/vote/${idVoto}`,{
      vote: true
    }, {
      headers: {
        'authorization': token
      }
    })
      .then(dat => {
        setVotado(true);
        console.log(dat);

        setIdea({ ...idea, votos: cantVotos(dat.data) })
      })
      .catch(console.log)
    }else{
    axios.post(`http://localhost:5000/v1/api/vote/${datos._id}`
    ,{
      vote: true
    }, {
      headers: {
        'authorization': token
      }}
    )
      .then(dat => {
        setVotado(true);
        console.log(dat);

        setIdea({ ...idea, votos: cantVotos(dat.data) })
      })
      .catch(console.log)
    }

  }

  
  
  const upclass = ()=>{
    if(votado === true){
      console.log('true');

      return 'voteup'
    }else{
      return 'upvote'
    }
   
   
    }

  const downclass = ()=>{
    if(votado === false){
      console.log('false');
      return 'votedown'
    }else{
      return 'downvote'
    }
   
   
    }
  
  return (
    <>
      <Card
        bg={'transparent'}
        key={idea.id}
        text={'dark'}
        className=" mt-5 shadow"
      >
        <Card.Header className='fw-bold'>{idea.titulo}</Card.Header>
        <Card.Body>
          <Card.Text>
            {idea.descripcion}
          </Card.Text>
          <footer className="blockquote-footer">
            Author: <cite title="Source Title">{idea.autor}</cite>
          </footer>
         
        </Card.Body>
        <Card.Footer >
          <div className='d-flex justify-content-around'>
          <h2>Puntos: {datos.vote.length}</h2>
          {token?
         <form onSubmit={upVote}>
         <input type="hidden" name="up" value={datos._id} />
         <button className={upclass()}type="submit"></button>
       </form>:
          <form>
          <BtnSesion tipo='upvote'></BtnSesion>
          </form>
        
        
        }
          {token?
            <form onSubmit={downVote}>
            <input type="hidden" name="down" value={datos._id} />
            <button className={downclass()}type="submit"></button>
          </form>:
          <form>
          <BtnSesion tipo='downvote'></BtnSesion>
          </form>
        
        
        }
        
          </div>
          <hr />
     <Link to={`/idea/${idea.id}`}className='text-center  text-decoration-none'style={{color:'#2b86c5'}} ><h3 >Comentarios{`(${idea.comentarios})`}</h3></Link>
        </Card.Footer>
      </Card>
    </>

  )
}

export default Idea
