import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Eliminar = () => {
  let valorInicial = false;
  let { ideaID } = useParams();

    axios.get(`http://localhost:5000/v1/api/idea/${ideaID}`)
      .then((dat) => {
        const userLocalStorage = localStorage.getItem('user').replace(/[ '"]+/g, '').trim();

        if (dat.data.author._id !== userLocalStorage) {
          return window.location = '/';
        }else{
          valorInicial = true;
        }
        
      })
      .catch(console.log)



  const [show, setShow] = useState(valorInicial);
  const handleClose = () => {
    if (show === true) {
      setShow(false);

    } else {
      setShow(true)
    }
  };
  const deleteIdea = () => {
    const token = document.cookie.replace('token=', '');
    axios.delete('http://localhost:5000/v1/api/idea/' + ideaID,
      {
        headers: {
          'Authorization': token
        }
      })
      .then(comentariosapi => {
        console.log(comentariosapi)
      })
      .catch(console.log)
  }



  return (
    <>


      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>¿Está seguro que desea eliminar dicha idea?</Modal.Title>
        </Modal.Header>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="danger" onClick={deleteIdea}>
            Eliminar
          </Button>

        </Modal.Footer>
      </Modal>
    </>
  );
}
export default Eliminar
