import React from 'react';
import { Card,Button } from 'react-bootstrap';

const IdeaCRUD = ({datos}) => {
 
  return (
    <>

    <Card
      bg={'light'}
      key={datos._id}
      text={'dark'}
      className="mb-2 mt-5 shadow"
    >
      <Card.Header className='fw-bold'>{datos.idea}</Card.Header>
     <Card.Body>
     <h2 className='text-success '>Votos positivos: {datos.upvotes.length}</h2>
     <h2 className='text-danger '>Votos negativos: {datos.downvotes.length}</h2>
     {
      datos.upvotes.length - datos.downvotes.length >= 0 ?
<h2 className='text-light d-inline-block bg-success'>Tú puntaje: {datos.upvotes.length - datos.downvotes.length}</h2>
:
<h2 className='text-light d-inline-block bg-danger'>Tú puntaje: {datos.upvotes.length - datos.downvotes.length}</h2>

     }
     <h2 className='text-secondary'>Cantidad total de votos: {datos.upvotes.length + datos.downvotes.length}</h2>
     <h2 className='text-primary'>Cantidad total de comentarios: {datos.comments.length}</h2>




     </Card.Body>
      <Card.Footer className='d-flex justify-content-around'>
    <Button className="btn btn-secondary" href={`/editar/${datos._id}`}>Editar</Button>
    <Button className="btn btn-danger" href={`/eliminar/${datos._id}`}>Eliminar</Button>
      </Card.Footer>
    </Card>


  </>
)
}

export default IdeaCRUD
