import React, { useState } from 'react'
import { Button, Form, InputGroup } from 'react-bootstrap'

const Config = () => {
  const [passClick, setPassClick] = useState(false);
  const [usernameClick, setUsernameClick] = useState(false);
  const [nameClick, setNameClick] = useState(false);



  function editarCampo(valor, setValor) {
    if (valor === true) {
      setValor(false);
    } else {
      setValor(true);
    }
  }
  return (
    <div className=' d-flex align-items-center  justify-content-center '>
      <Form className='form-config rounded shadow mt-5'>
        <InputGroup className=' d-flex align-items-center justify-content-between mb-5' >
          <label className='fw-bold' htmlFor="cantidad">Nombre</label>
          {
            nameClick === false ? <input  className='bg-dark text-light p-1 shadow border-0 rounded' type="text" name="cantidad" id="cantidad" readonly="readonly" value='jhon pene' />
              : <input type="text"  className='bg-light  p-1 shadow border-0 rounded' name="cantidad" id="cantidad" value='jhon pene' autoFocus />

          }
          <Button className='shadow' variant='secondary' onClick={() => editarCampo(nameClick, setNameClick)}>Edit</Button>
        </InputGroup>
        <InputGroup className=' d-flex align-items-center justify-content-between mb-5'>
          <label className='fw-bold' htmlFor="username">Username</label>
          {
            usernameClick === false ? <input  className='bg-secondary' type="text" name="username" id="username" readonly="readonly" value='admin' />
              : <input type="text" name="username" id="username" value='admin' autoFocus/>


          }
          <Button variant='secondary' onClick={() => editarCampo(usernameClick, setUsernameClick)}>Edit</Button>
        </InputGroup>
        <InputGroup className=' d-flex align-items-center justify-content-between mb-5'>
          <label className='fw-bold' htmlFor="pass">Password</label>
          {
            passClick === false ? <input className='bg-secondary'type="password" name="pass" id="pass" readonly="readonly" value='1234' />
              : <input type="text" name="pass" id="pass" value='1234' autoFocus/>



          }
          <Button variant='secondary' onClick={() => editarCampo(passClick, setPassClick)}>Edit</Button>
        </InputGroup>
        <Button variant='success'>Guardar Cambios</Button>
      </Form>

    </div>
  )
}

export default Config
