import React, { useState } from 'react'
import { Button, Form, InputGroup } from 'react-bootstrap'

const Config = () => {
  const [passClick, setPassClick] = useState(false);
  const [usernameClick, setUsernameClick] = useState(false);
  const [nameClick, setNameClick] = useState(false);



  function editarCampo(valor,setValor){
    if(valor === true){
      setValor(false);
    }else{
      setValor(true);
    }
  }
  return (
    <div className=' d-flex align-items-center  justify-content-center '>
        <Form className='form-config rounded shadow mt-5'>
            <InputGroup  className=' d-flex align-items-center justify-content-between mb-5' >
            <label htmlFor="cantidad">Nombre</label>
            <input type="text" name="cantidad" id="cantidad" readonly="readonly" value='jhon pene' />
            <Button variant='secondary'>Edit</Button>
            </InputGroup>
            <InputGroup className=' d-flex align-items-center justify-content-between mb-5'>
            <label htmlFor="username">Username</label>
            <input type="text" name="username" id="username" readonly="readonly" value='admin' />
            <Button variant='secondary'>Edit</Button>
            </InputGroup>
            <InputGroup className=' d-flex align-items-center justify-content-between mb-5'>
            <label htmlFor="pass">Password</label>
            {
              passClick === false? <input type="password" name="pass" id="pass" readonly="readonly" value='1234' />
              :<input type="text" name="pass" id="pass" value='1234' />



            }
            <Button variant='secondary' onClick={()=>editarCampo(passClick,setPassClick)}>Edit</Button>
            </InputGroup>
            <Button variant='success'>Guardar Cambios</Button>
        </Form>

    </div>
  )
}

export default Config
