import React, {useState} from 'react';
import {Container} from 'react-bootstrap';
import './registro.css';
import axios from 'axios';


const RegistroMainMenu = () => {
  if(document.cookie.length >0 ){
    window.location = "/"
  }
  
    const [user,setUser] = useState("");
    const [name,setName] = useState("");
    const [pass,setPass] = useState("");
  
    const handleSubmit = e=>{
      e.preventDefault();
      axios.post('http://localhost:5000/v1/api/auth/signup', {
        username: user,
        name: name,
        password: pass     
      })
      .then(function (response) {
        window.location = "/v1/api/auth/signin";
      })  
      .catch(function (error) {
        console.log(error);
      });
     
     
    }
  return (
    <Container>
  <div className="login">
  <div className="login-triangle " />
  <h2 className="login-header bg-primary">Registrate!</h2>
  <form className="login-container" onSubmit={handleSubmit}>
  <input className='mb-3' type="text" name="username" value={user}  onChange={(e)=>setUser(e.target.value)}  placeholder="user" />
  <input className='mb-3' type="text" name="name" value={name}  onChange={(e)=>setName(e.target.value)}  placeholder="name" />
      <input className='mb-3' type="password" name="password" value={pass}  onChange={(e)=>setPass(e.target.value)} placeholder="Password" />
      <input type="submit" value="Registrate!" />
  </form>
</div>
  </Container>
  )
}

export default RegistroMainMenu
