import React, {useEffect} from 'react';

import {useIdea,IdeaProvider} from '../../context/idea.context'
import Idea from '../../componentes/idea/Idea';

export default () => <IdeaProvider>
  <TopIdeas></TopIdeas>
</IdeaProvider>
const TopIdeas = () => {
    const {  getIdeas,
        ideas} = useIdea();
        //const [topsIdeas,setTopsIdeas] = useState();

        useEffect(()=>{
          getIdeas();
          console.log(ideas);
        },[])
        
        let masVotados = ideas.filter(vote=>{return vote.upvotes.length - vote.downvotes.length>0}).sort((a,b)=>{
            const votosA = a.upvotes.length - a.downvotes.length;
            const votosB = b.upvotes.length - b.downvotes.length;
            return votosB - votosA;
        })
        let masComentados = ideas.filter(vote=>{return vote.comments.length>0}).sort((a,b)=>{            
            const commentsA = a.comments.length;
            const commentsB = b.comments.length;           
            return commentsB - commentsA;
        })
        masComentados.length = 2;
        masVotados.length = 2;


  return (
    <div className='container'>
        
<h1>Top 10 ideas más votadas</h1>

{
        masVotados.length > 0 &&
        masVotados.map((idea) => <a href={`/idea/${idea._id}`} key={idea._id} style={{textDecoration: 'none'}}><Idea datos={idea} key={idea._id} /> </a>)
      }
      <h1>Top 10 ideas más Comentadas</h1>

    {
        masComentados.length > 0 &&
        masComentados.map((idea) => <a href={`/idea/${idea._id}`} key={idea._id} style={{textDecoration: 'none'}}><Idea datos={idea} key={idea._id} /> </a>)
      }

    </div>
  )
}
