import React,{useState, useEffect} from 'react';
import Loading from '../../components/loading/Loading'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const MyAccount = () => {
    const token = document.cookie.replace('token=', '');
    const user = JSON.parse(localStorage.getItem('user'));
    const [userData, setUserData] = useState('');
    const [userStats, setUserStats] = useState('');
    const navigate = useNavigate();
    useEffect(()=>{
        if (!token || !user) {
            navigate('/');
          } 
    },[]);
    useEffect(()=>{
        axios.get(`http://localhost:5000/v1/api/user/${user}`)
        .then((response)=>{
            const data = {
                username: response.data.username,
                name: response.data.name
            }
            setUserData(data);
            })
        .catch(console.log)
    },[]);
    useEffect(()=>{
        axios.get(`http://localhost:5000/v1/api/idea`)
        .then((response)=>{
          const ideasTotal = response.data.filter(idea => {return idea.author._id === user}).length;
          let positiveVoted = 0;
          let negativeVoted = 0;
          let ideasKarma = 0;
            response.data.forEach((el)=>{
                if(el.author._id === user){
                    if(el.vote.length >0){
                        el.vote.forEach(vote=>{
                        
                            if(vote.vote === true){
                                ideasKarma+=1;
                            }else{
                                ideasKarma-=1;
    
                            }
                        })
                    }
                  
                }
                const positiveUserVoted = el.vote.filter((el)=>{return el.author._id === user && el.vote === true}).length;
                const negativeUserVoted = el.vote.filter((el)=>{return el.author._id === user && el.vote === false}).length;
                positiveVoted+= positiveUserVoted;
                negativeVoted+= negativeUserVoted;
                });
                const stats = {
                    ideasTotal,
                    positiveVoted,
                    negativeVoted,
                    ideasKarma
                }
                setUserStats(stats);
                })
        .catch(console.log)
    },[userData !== '']);

  return (
    <div>
        {
            userData && userStats?
            <div> 
            <h1>Username: {userData.username}</h1>
            <h2>name: {userData.name}</h2>
            <p>My ideas: {userStats.ideasTotal}</p>
            <p>Positive voted: {userStats.positiveVoted}</p>
            <p>Negative voted: {userStats.negativeVoted}</p>
            <p>Ideas Karma: {userStats.ideasKarma}</p>




            </div>
            :
            <Loading></Loading>
        }
 <div className="row container d-flex justify-content-center">
            <div className="col-xl-6 col-md-12">
                <div className="card user-card-full">
                    <div className="row m-l-0 m-r-0">
                        <div className="col-sm-4 bg-c-lite-green user-profile">
                            <div className="card-block text-center text-white">
                                <div className="m-b-25">
                                    <img src="https://img.icons8.com/bubbles/100/000000/user.png" className="img-radius" alt="User-Profile-Image"/>
                                </div>
                                <h6 className="f-w-600">Hembo Tingor</h6>
                                <p>Web Designer</p>
                                <i className=" mdi mdi-square-edit-outline feather icon-edit m-t-10 f-16"></i>
                            </div>
                        </div>
                        <div className="col-sm-8">
                            <div className="card-block">
                                <h6 className="m-b-20 p-b-5 b-b-default f-w-600">Information</h6>
                                <div className="row">
                                    <div className="col-sm-6">
                                        <p className="m-b-10 f-w-600">Email</p>
                                        <h6 className="text-muted f-w-400">rntng@gmail.com</h6>
                                    </div>
                                    <div className="col-sm-6">
                                        <p className="m-b-10 f-w-600">Phone</p>
                                        <h6 className="text-muted f-w-400">98979989898</h6>
                                    </div>
                                </div>
                                <h6 className="m-b-20 m-t-40 p-b-5 b-b-default f-w-600">Projects</h6>
                                <div className="row">
                                    <div className="col-sm-6">
                                        <p className="m-b-10 f-w-600">Recent</p>
                                        <h6 className="text-muted f-w-400">Sam Disuja</h6>
                                    </div>
                                    <div className="col-sm-6">
                                        <p className="m-b-10 f-w-600">Most Viewed</p>
                                        <h6 className="text-muted f-w-400">Dinoter husainm</h6>
                                    </div>
                                </div>
                                <ul className="social-link list-unstyled m-t-40 m-b-10">
                                    <li><a href="#!" data-toggle="tooltip" data-placement="bottom" title=""
                                            data-original-title="facebook"
                                            data-abc="true"><i className="mdi mdi-facebook feather icon-facebook facebook" aria-hidden="true"></i></a>
                                    </li>
                                    <li><a href="#!" data-toggle="tooltip" data-placement="bottom" title=""
                                            data-original-title="twitter"
                                            data-abc="true"><i className="mdi mdi-twitter feather icon-twitter twitter" aria-hidden="true"></i></a>
                                    </li>
                                    <li><a href="#!" data-toggle="tooltip" data-placement="bottom" title=""
                                            data-original-title="instagram"
                                            data-abc="true"><i className="mdi mdi-instagram feather icon-instagram instagram" aria-hidden="true"></i></a>
                                    </li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    </div>
  )
}

export default MyAccount
