import React, {useState}from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';
import Idea from '../../componentes/idea/Idea';
import {IdeaProvider} from '../../context/idea.context'
export default <IdeaProvider> <IdeaIndividual/></IdeaProvider>
const IdeaIndividual = () => {
  const [datos,setDatos] = useState('');
  let {ideaID} = useParams();

      useEffect(()=>{
        axios.get('http://localhost:5000/v1/api/idea/'+ideaID)
        .then(dat => {
          const data = dat.data;
         setDatos(data);
         console.log(datos);
  
        })
        .catch(console.log)
      },[!datos])
  

  return (
    <div className='container'>
        {datos && <Idea datos={datos}></Idea>}
        {!datos && <h1>No se cargaron los datos</h1>}
    </div>
  )
}
