import React , {useState,useEffect}from 'react';

const MasComentadosMain = () => {
    const [copia, setCopia]  = useState([]);
    useEffect(()=>{
      let datos;
      fetch('./prueba.json')
      .then(dat =>{
          datos = dat.data;
          let datosComentados = datos.sort((a, b) => b.comments.length - a.comments.length)
          setCopia(datosComentados);
      console.log(datosComentados);
      
      }, [])
      .catch(console.log)  
    },[])
       
  
  
  return (
    <div className='container'>
      
{
  copia&&
    copia.map((idea) => idea)
  }



   </div>
  )
}


export default MasComentadosMain
