import React, {useState} from 'react';
import {Container} from 'react-bootstrap';
import './login.css';
import axios from 'axios';





const Login = () => {
  const [user,setUser] = useState("");
  const [pass,setPass] = useState("");

  const handleSubmit = e=>{
    e.preventDefault();
    axios.post('http://localhost:5000/v1/api/auth/signin', {
      username: user,
      password: pass     
    })
    .then(function (response) {
      response.headers.Authorization = response.data.token;
      localStorage.setItem('token',response.data.token)
    })
    .catch(function (error) {
      console.log(error);
    });
   
    console.log(`El usuario es: ${user} y la contrase√±a: ${pass}`);
  }
  return (
    <Container>
  <div className="login">
  <div className="login-triangle" />
  <h2 className="login-header bg-primary">Log in</h2>
  <form className="login-container" onSubmit={handleSubmit} >
  <input className='mb-3' type="text" name="username" value={user}  onChange={(e)=>setUser(e.target.value)}  placeholder="user" />
      <input className='mb-3' type="password" name="password" value={pass}  onChange={(e)=>setPass(e.target.value)} placeholder="Password" />
      <input type="submit" value="Ingresar" />
  </form>
</div>
  </Container>
  )
}

export default Login
