import React from 'react';
import {useIdea} from '../../context/idea.context'
import { Card, Button } from 'react-bootstrap';

const Idea = ({ datos }) => {
 
  const {votos, upVote,downVote,getIdeas} = useIdea();
  const votoPositivo = e =>{
    e.preventDefault();
    upVote(datos._id);
    getIdeas();
  }
  const votoNegativo = e =>{
    e.preventDefault();
    downVote(datos._id);
    getIdeas();
  }

  return (

    <>

      <Card
        bg={'light'}
        key={datos._id}
        text={'dark'}
        className="mb-2 mt-2"
      >
        <Card.Header>{datos.idea}</Card.Header>
        <Card.Body>
          <Card.Text>
            {datos.description}
          </Card.Text>
          <footer className="blockquote-footer">
            Author: <cite title="Source Title">{datos.author.name}</cite>
          </footer>
        </Card.Body>
        <Card.Footer>
          <h2>Puntos: {votos }</h2>
          <form action={`http://localhost:5000/v1/api/idea/${datos._id}/upvote`} onSubmit={votoPositivo}>
            <input type="hidden" name="up" value={datos._id} />
            <Button type="submit">positivo</Button>
          </form>
          <form action={`http://localhost:5000/v1/api/idea/${datos._id}/downvote`}  onSubmit={votoNegativo}>

            <input type="hidden" name="down" value={datos._id} />

            <Button className="btn-danger" type="submit">negativo</Button>
          </form>



        </Card.Footer>
      </Card>


    </>

  )
}

export default Idea
