import React,{useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import IdeaCRUD from '../../componentes/idea/IdeaCRUD';

const MisIdeas = () => {
    const [ideas,setIdeas] = useState('');
    const userID = JSON.parse(localStorage.getItem('user'));
    const token = document.cookie.replace('token=', '');
    const navigate = useNavigate();
if(!token){
navigate('/');  
}
console.log(ideas);
    useEffect(()=>{
        axios.get(`http://localhost:5000/v1/api/idea/${userID}/all`,{
          headers: {
              'Authorization': token
          }
      })
        .then((respuesta)=>{
          
            if(respuesta.status !== 200 && respuesta.status !== 201){
              document.cookie = "token=; max-age=0";
    localStorage.removeItem('user');
              navigate('/');
            }  
            setIdeas(respuesta.data);
          })
        .catch(()=>{
          document.cookie = "token=; max-age=0";
    localStorage.removeItem('user');
    navigate('/');
  })
    },[!ideas])
  
  return (
    <div className='container'>
        <div className="d-flex align-items-center mt-5 justify-content-center">
        <a className='btn btn-success text-center bold' href={"/crear"} style={{width: '200px'}}>Crear nueva idea</a>

        </div>

        {ideas.length > 0 && ideas.map((idea)=>{return <IdeaCRUD datos={idea} key={ideas._id}/>})}
        {ideas.length === 0 &&<h1>Usted no tiene ideas!</h1>}
        {ideas === '' && <h1>Cargando..</h1>}

    </div>
  )
}

export default MisIdeas
