import React from 'react';
import { useParams } from 'react-router-dom';
import Idea from '../../componentes/idea/Idea';
import {useIdea,IdeaProvider} from '../../context/idea.context';
export default () => <IdeaProvider>
<IdeaIndividual></IdeaIndividual>
</IdeaProvider>

const IdeaIndividual = () => {
  let {ideaID} = useParams();
  let {getIdea} = useIdea();
 const datosIdea =  getIdea(ideaID);
 console.log(datosIdea);

  return (
    <div className='container'>
      <Idea datos={datosIdea}/>
    </div>
  )
}
