import React, {useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import Loading from '../loading/Loading';
const AlertaEliminar = (props) => {
    useEffect(() => {
        axios.get(`http://localhost:5000/v1/api/idea/${props.ideaID}`)
          .then((dat) => {
            const userLocalStorage = localStorage.getItem('user').replace(/[ '"]+/g, '').trim();
    
            if (dat.data.author._id !== userLocalStorage) {
              return window.location = '/';
            }})
          .catch(console.log)
      }, [])
    
    
    
   
      const deleteIdea = () => {
        const token = document.cookie.replace('token=', '');
        axios.delete('http://localhost:5000/v1/api/idea/' + props.ideaID,
          {
            headers: {
              'Authorization': token
            }
          })
          .then((data) => {
              console.log(data);
                  })
          .catch(console.log)
      }
    
    return (
        <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
     
        centered
    >
        <Modal.Header closeButton className='bg-light'>
            <Modal.Title id="contained-modal-title-vcenter">
            ¿Estás seguro que deseas eliminar  
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
         Esta acción es irreversible
         <Loading/>
        </Modal.Body>
        <Modal.Footer>
            <Button onClick={props.onHide} className='btn-secondary'>Close</Button>
            <Button variant="danger"  onClick={deleteIdea} >
        Eliminar
      </Button>
        </Modal.Footer>
    </Modal>
    );
}

export default AlertaEliminar;
