import React , {useState,useEffect}from 'react';
import axios from 'axios';
import Idea from './Idea';

const IniciarApp = () => {
    const [copia, setCopia]  = useState([]);
    useEffect(()=>{
      let datos;
      axios.get('http://localhost:5000/v1/api/idea')
      .then(dat =>{
          datos = dat.data;
          setCopia(datos);
      console.log(datos);
      
      }, [])
      .catch(console.log)  
    },[])
       
  
  
  return (
    <div className='container'>
      
{
  copia&&
    copia.map((idea) => <Idea datos={idea}/>)
  }



   </div>
  )
}

export default IniciarApp
