import React, {useEffect} from 'react';

import {useIdea,IdeaProvider} from '../../context/idea.context'
import Idea from '../../componentes/idea/Idea';

export default () => <IdeaProvider>
  <Home></Home>
</IdeaProvider>
const Home = () => {
  const {  getIdeas,
    ideas} = useIdea();
    useEffect(()=>{
      getIdeas();
      console.log(ideas);
    },[])


  return (
    <div className='container'>

      {
        ideas.length > 0 &&
        ideas.map((idea) => <a href={`http:localhost:3000/idea/${idea._id}`} style={{textDecoration: none}}><Idea datos={idea} key={idea._id} /> </a>)
      }



    </div>
  )
}
