import React , {useState,useEffect}from 'react';
import IndividualComment from './IndividualComment';
import axios from 'axios';


const Comments = ({commentsID}) => {   
    const [comentarios, setComentarios] = useState([{
        autor: 'elmenco',
        comentario: 'Esta bien perrona la idea bro!!! Felicitaciones!!!'
    },
    {
        autor: 'iara',
        comentario: 'Todo de 10 bro'
    },
    {
        autor: 'picu',
        comentario: 'Es el futuro!!!'
    }]);
    const [comentarioIndividual, setComentarioIndividual] = useState('');

    function obtenerComentarios(){
        axios.get('http://localhost:5000/v1/api/comment/'+commentsID)
        .then(comentariosapi =>{
            setComentarios(comentariosapi);
        })
    }
    function comentar(e){
        e.preventDefault();
        axios.post('http://localhost:5000/v1/api/comment/'+commentsID,{
            'comment':comentarioIndividual
        })
        .then(comentariosapi =>{
            setComentarios(comentariosapi);
        })
    }
    useEffect(()=>{
       obtenerComentarios();
    },[])



  return (

<div className='pb-3'>
<h1 className='text-uppercase text-primary  fw-bold text-center '>comentarios {`(${comentarios.length})`} </h1>
{comentarios&& comentarios.map((el)=>{
  return  <IndividualComment autor={el.autor} comentario={el.comentario}></IndividualComment>
})}


    <form className='d-flex justify-content-center  col-12' onSubmit={comentar}>
        <textarea name="comentario" id="comentario" placeholder='Escribe tÃº comentario...' className='col-6 me-2 p-1 rounded-3 border border-primary border-2 '  style={
           { 'max-height': '60px'
            }

        } value={comentarioIndividual} onChange={(e)=>{setComentarioIndividual(e.target.value)}}></textarea>
        <input type="submit" className='btn btn-primary' value={'comentar'} />
    </form>
    </div>
  )
}

export default Comments
