import React, { useState } from 'react';
import IndividualComment from './IndividualComment';
import axios from 'axios';


const Comments = (data) => {
  const [comentarios, setComentarios] = useState(data.data.comments);
  const [comentarioIndividual, setComentarioIndividual] = useState('');
  const token = document.cookie.replace('token=', '');

  function comentar() {

    axios.post('http://localhost:5000/v1/api/comment/' + data.data._id,
      {
        comment: comentarioIndividual
      },
      {
      headers: {
        'Authorization': token
      }
    })
      .then(comentariosapi => {
        setComentarios(comentariosapi);
      })
  }



  return (

    <div className='pb-3 mt-5'>
      <div className="d-flex justify-content-center ">
        <h1 className='text-uppercase text-primary d-inline-block   fw-bold   '>comentarios</h1>

      </div>
      {comentarios && comentarios.map((el) => {
        return <IndividualComment autor={el.author.name} comentario={el.comment}></IndividualComment>
      })}


      <form className='d-flex justify-content-center  mt-5 col-12' onSubmit={comentar}>
        <textarea name="comentario" id="comentario" placeholder='Escribe tÃº comentario...' className='col-6 me-2 p-1 rounded-3 border border-primary border-2 ' style={
          {
            'max-height': '60px'
          }

        } value={comentarioIndividual} onChange={(e) => { setComentarioIndividual(e.target.value) }}></textarea>
        <input type="submit" className='btn btn-primary' value={'comentar'} />
      </form>
    </div>
  )
}

export default Comments
