import React,{useState} from 'react';
import axios from 'axios';
import {Card,ToggleButton,ToggleButtonGroup} from 'react-bootstrap';

const Idea = ({datos}) => {
  const [value, setValue] = useState([1, 3]);
  const token = document.cookie.replace('token=','');
  const downVote = ()=>{
axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/downvote`)
.then(dat=>console.log(dat.data))
.catch(console.log)
  }

  const upVote = ()=>{
    axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/upvote`)
    .then(dat=>console.log(dat.data))
    .catch(console.log)
      }

  const handleChange = (val) => setValue(val);

  return (
  
      <>
     
        <Card
          bg={'light'}
          key={datos._id}
          text={'dark'}
          className="mb-2 mt-2"
        >
          <Card.Header>{datos.idea}</Card.Header>
          <Card.Body>
            <Card.Text>
            {datos.description}
            </Card.Text>
            <footer className="blockquote-footer">
           Author: <cite title="Source Title">{datos.author.name}</cite>
          </footer>
          </Card.Body>
          <Card.Footer>
            <h2>Puntos: 10</h2>
          <ToggleButtonGroup type="checkbox" value={value} onChange={handleChange}>
      <ToggleButton id="tbg-btn-1" value={1} onClick={upVote()}>
Positivo      </ToggleButton>
      <ToggleButton id="tbg-btn-2" value={2} className='bg-danger'  onClick={downVote()}>
Negativo      </ToggleButton>
    
    </ToggleButtonGroup>
          </Card.Footer>
        </Card>
        
      
    </>
   
    )
}

export default Idea
