import React from 'react';
import { Card, Button } from 'react-bootstrap';
import BtnEliminar from '../alertas/btn_alertas/BtnEliminar';
const IdeaCRUD = ({ datos }) => {
  return (
    <>
      <Card
        bg={'transparent'}
        key={datos._id}
        text={'dark'}
        className="mb-2 mt-5 shadow"
      >
        <Card.Header className='fw-bold'>{datos.idea}</Card.Header>
        <Card.Body>
          <h4 className='text-success '>Votos positivos: {datos.vote.length}</h4>
          <h4 className='text-danger '>Votos negativos: {datos.vote.length}</h4>
          {
            datos.vote.length >= 0 ?
              <h2 className='text-light d-inline-block p-1 rounded bg-success'>Tú puntaje: {datos.vote.length }</h2>
              :
              <h2 className='text-light d-inline-block p-1 rounded bg-danger'>Tú puntaje: {datos.vote.length}</h2>
          }
          <h4 className='text-secondary'>Cantidad total de votos: {datos.vote.length}</h4>
          <h4 className='text-primary'>Cantidad total de comentarios: {datos.comments.length}</h4>
        </Card.Body>
        <Card.Footer className='d-flex justify-content-around'>
          <Button variant="outline-primary" href={`/editar/${datos._id}`}>Editar</Button>
          <BtnEliminar ideaID={datos._id} />
        </Card.Footer>
      </Card>
    </>
  )
}

export default IdeaCRUD
