import React, { useState } from 'react'
import Alerta_sesion from '../Alerta_sesion';
import { Button } from 'react-bootstrap';

const Btn_sesion = (props) => {
    const config = {
        boton: props.boton,
        textoBtn: props.textoBtn
    }
    const [modalShow, setModalShow] = useState(false);

    return (
        <>

           
                <Button variant={config.boton} onClick={() => setModalShow(true)}>
{config.textoBtn}     </Button>

                <Alerta
                    show={modalShow}
                    title='Debes tener una cuenta para realizar esta acciÃ³n!'  
                    onHide={() => setModalShow(false)}
                />


        </>);
}

export default Btn_sesion;
