import React, { useState } from 'react'
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Form, InputGroup } from 'react-bootstrap'
import axios from 'axios';

const Config = () => {
  const navigate = useNavigate();
  const token = document.cookie.replace('token=', '');
  const user = JSON.parse(localStorage.getItem('user'));
  const [passClick, setPassClick] = useState(false);
  const [usernameClick, setUsernameClick] = useState(false);
  const [nameClick, setNameClick] = useState(false);
  const [name,setName] = useState('');
  const [username,setUsername] = useState('');
  useEffect(()=>{
    if (!token || !user) {
      navigate('/');
  }
  },[])
  useEffect(()=>{
    axios.get(`http://localhost:5000/v1/api/user/${user}`)
    .then((response)=>{
      console.log(response);
      setName(response.data.name);
      setUsername(response.data.username);
    })
  },[])

  function editarCampo(valor, setValor) {
    if (valor === true) {
      setValor(false);
    } else {
      setValor(true);
    }
  }
  return (
    <div className=' d-flex align-items-center  justify-content-center '>
      <Form className='form-config rounded shadow mt-5'>
        <InputGroup className=' d-flex align-items-center justify-content-between mb-5' >
          <label className='fw-bold' htmlFor="cantidad">Nombre</label>
          {
            nameClick === false ? <input  className='bg-dark text-light p-1 shadow border-0 rounded' type="text" name="cantidad" id="cantidad" readonly="readonly" value={name} />
              : <input type="text"  className='bg-light  p-1 shadow border-0 rounded' name="cantidad" id="cantidad" value={name} onChange={(e)=>setName(e.target.value)} />

          }
          <Button className='shadow' variant='secondary' onClick={() => editarCampo(nameClick, setNameClick)}>Edit</Button>
        </InputGroup>
        <InputGroup className=' d-flex align-items-center justify-content-between mb-5'>
          <label className='fw-bold' htmlFor="username">Username</label>
          {
            usernameClick === false ? <input   className='bg-dark text-light p-1 shadow border-0 rounded' type="text" name="username" id="username" readonly="readonly" value='admin' />
              : <input type="text" className='bg-light  p-1 shadow border-0 rounded' name="username" id="username" value='admin' />


          }
          <Button variant='secondary' onClick={() => editarCampo(usernameClick, setUsernameClick)}>Edit</Button>
        </InputGroup>
        <InputGroup className=' d-flex align-items-center justify-content-between mb-5'>
          <label className='fw-bold' htmlFor="pass">Password</label>
          {
            passClick === false ? <input  className='bg-dark text-light p-1 shadow border-0 rounded' type="password" name="pass" id="pass" readonly="readonly" value='1234' />
              : <input type="text" className='bg-light  p-1 shadow border-0 rounded' name="pass" id="pass" value='1234' />



          }
          <Button variant='secondary' onClick={() => editarCampo(passClick, setPassClick)}>Edit</Button>
        </InputGroup>
        <Button variant='success'>Guardar Cambios</Button>
      </Form>

    </div>
  )
}

export default Config
