import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';

const Eliminar = ({id}) => {
    const valorInicial = true;
    const [show, setShow] = useState(valorInicial);
    const handleClose = () => {
      if (show === true) {
        setShow(false);
  
      } else {
        setShow(true)
      }
    };
    const eliminar = ()=>{
      const token = document.cookie.replace('token=', '');

      axios.delete('http://localhost:5000/v1/api/idea/' + id,    
      {
      headers: {
        'Authorization': token
      }
    })
      .then(comentariosapi => {
        console.log(comentariosapi)
      })
      .catch(console.log)
  }
    
  
  
    return (
      <>
  
  
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>¿Está seguro que desea eliminar dicha idea?</Modal.Title>
          </Modal.Header>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="danger" onClick={eliminar}>
              Eliminar
            </Button>
      
          </Modal.Footer>
        </Modal>
      </>
    );
  }
export default Eliminar
