import React from 'react';
import axios from 'axios';
import {Card,Button} from 'react-bootstrap';

const Idea = ({datos}) => {

  const cantVotos = ()=>{
    return datos.upvotes.length - datos.downvotes.length;
      }
  const token = document.cookie.replace('token=','');
  const downVote = ()=>{
axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/downvote`, {
  headers:{
    'authorization': token
  }
  
})
.then(dat=>console.log(dat.data))
.catch(console.log)
  }

  const upVote = ()=>{
    axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/upvote`, {
      headers:{
        'authorization': token
    
      }
      
    })
    .then(dat=>console.log(dat.data))
    .catch(console.log)
      }


  return (
  
      <>
     
        <Card
          bg={'light'}
          key={datos._id}
          text={'dark'}
          className="mb-2 mt-2"
        >
          <Card.Header>{datos.idea}</Card.Header>
          <Card.Body>
            <Card.Text>
            {datos.description}
            </Card.Text>
            <footer className="blockquote-footer">
           Author: <cite title="Source Title">{datos.author.name}</cite>
          </footer>
          </Card.Body>
          <Card.Footer>
            <h2>Puntos: {cantVotos()}</h2>
     
      <Button   onClick={upVote()}>
Positivo      </Button>
      <Button  className='bg-danger'  onClick={downVote()}>
Negativo      </Button>
    
          </Card.Footer>
        </Card>
        
      
    </>
   
    )
}

export default Idea
