import React, { useState } from 'react';
import { Container, Alert } from 'react-bootstrap';
import { useHistory } from 'react-router-dom'; 
import './form.css';
import axios from 'axios';


const Registro = () => {
  const history = useHistory();
  if (document.cookie.length > 10) {
history.push('/');
 }

  const [user, setUser] = useState("");
  const [name, setName] = useState("");
  const [pass, setPass] = useState("");
  const [error, setError] = useState("");


  const handleSubmit = e => {
    e.preventDefault();
    if (!user) {
      setError('usuario');
      return;
    }
    if (!name) {
      setError('nombre');
      return;
    }
    if (!pass) {
      setError('contraseña');
      return;
    }

    setError("");
      axios.post('http://localhost:5000/v1/api/auth/signup', {
        username: user,
        name: name,
        password: pass
      })
        .then(function () {
          window.location = "/login";
        })
        .catch(function (error) {
          console.log(error);
        });


  }
  return (
    <Container>
      <div className="login">
        <div className="login-triangle " />
        <h2 className="login-header bg-dark fw-bold">Registrate!</h2>
        <form className="login-container shadow" onSubmit={handleSubmit}>
          <input className='mb-3' type="text" name="username" value={user} onChange={(e) => setUser(e.target.value)} placeholder="user" />
          <input className='mb-3' type="text" name="name" value={name} onChange={(e) => setName(e.target.value)} placeholder="name" />
          <input className='mb-3' type="password" name="password" value={pass} onChange={(e) => setPass(e.target.value)} placeholder="Password" />
          <input type="submit" value="Registrate!" />
        </form>
        {error && <Alert className='mt-3' variant='danger'>Debe completar el campo {error}</Alert>}

      </div>
    </Container>
  )
}

export default Registro
