import React,{useState,useEffect} from 'react';
import { useParams } from 'react-router-dom';
import {Form,Button,Alert} from 'react-bootstrap';
import axios from 'axios';

const CrearEditarForm = ({modo}) => {
const userLocalStorage = localStorage.getItem('user').replace(/[ '"]+/g, '').trim();


const [descripcion,setDescripcion] = useState('');
const [idea,setIdea] = useState('');
const [error, setError] = useState("");
let {ideaID} = useParams();
const token = document.cookie.replace('token=', '');




useEffect(()=>{
  if( modo === 'editar'){
    axios.get('http://localhost:5000/v1/api/idea/'+ideaID)
    .then(dat => {
      if(dat.data.author._id !== userLocalStorage){
        return  window.location = '/';
      }
     setIdea(dat.data.idea);  
     setDescripcion(dat.data.description);
    })
    .catch(console.log)
}

},[]);
if(idea.trim() === '' && error !== ''){
  setError('Debe completar el campo idea!');
  setTimeout(()=>{
    setError('');
  
  },3000)
  }

    const editar = (e)=>{
        e.preventDefault();
        console.log('editado!');
        axios.patch('http://localhost:5000/v1/api/idea/'+ideaID,{
          idea: idea,
          description: descripcion
        
      },{
          headers: {
              'Authorization': token
          }
      })
      .then(dat => {
        console.log(dat);
        const userLocalStorage = localStorage.getItem('user').replace(/[ '"]+/g, '').trim();

        if(dat.data.author._id !== userLocalStorage){
          console.log('axios: '+dat.data.author._id);
          console.log('LS: '+ userLocalStorage);

        }
      })
      .catch(console.log)

    }
    const crearIdea = (e)=>{
      e.preventDefault();
      console.log('create');

        axios.post('http://localhost:5000/v1/api/idea',{
            idea: idea,
            description: descripcion,
            author: {_id: userLocalStorage} ,
            comments: []
        },{
            headers: {
                'Authorization': token
            }
        }).then(
          (dat)=>{
            console.log(dat);

          }
        )
        .catch(console.log)
    }

  return (
    <div className="container mt-5">
        <h1>{ideaID ? ('Editar idea'): ('Crear nueva idea')}</h1>
    <Form  onSubmit={ideaID ? (editar):(crearIdea)}>
    {error &&

      <Alert className='mt-3' variant='danger'> {error}</Alert>



    }
        
    <Form.Group className="mb-3" >
      <Form.Label>Titulo de la idea</Form.Label>
      <Form.Control type="text" name="idea"  value={idea} onChange={(e) => setIdea(e.target.value)}/>

    </Form.Group>

    <Form.Group className="mb-3" controlId="formBasicPassword">
      <Form.Label>Descripcion de la idea</Form.Label>
      <Form.Control type="text" name="descripcion"  value={descripcion} onChange={(e) => setDescripcion(e.target.value)}/>
    </Form.Group>
 
    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form>
  </div>
    )
}

export default CrearEditarForm
