import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Form, Button, Alert } from 'react-bootstrap';
import axios from 'axios';

const CrearEditarForm = ({ modo }) => {
  const navigate = useNavigate();
  const userID = JSON.parse(localStorage.getItem('user'));

  const [descripcion, setDescripcion] = useState('');
  const [idea, setIdea] = useState('');
  const [error, setError] = useState("");
  const [exito, setExito] = useState("");

  let { ideaID } = useParams();
  const token = document.cookie.replace('token=', '');




  if (modo === 'editar') {
    useEffect(() => {

      axios.get('http://localhost:5000/v1/api/idea/' + ideaID)
        .then(dat => {
          if (dat.status !== 200 && dat.status !== 201) {
            document.cookie = "token=; max-age=0";
            return navigate('/');
          }
          setIdea(dat.data.idea);
          setDescripcion(dat.data.description);
        }
        )
        .catch(() => {
          document.cookie = "token=; max-age=0";
          localStorage.removeItem('user');
          navigate('/');
        })
    }, []);



  } else {
    useEffect(() => {

      axios.get(`http://localhost:5000/v1/api/idea/${userID}/all`, {
        headers: {
          'Authorization': token
        }
      })
        .then((respuesta) => {
          if (respuesta.status !== 200 && respuesta.status !== 201) {
            document.cookie = "token=; max-age=0";
            return navigate('/');
          }
        })
        .catch(() => {
          document.cookie = "token=; max-age=0";
          navigate('/');
        })
    }, []);

  }


  const editar = (e) => {

    e.preventDefault();
    console.log('editado!');

    if (idea.trim() === '') {
      setError('Debe completar el campo idea!');
    } else if (descripcion.trim() === '') {
      setError('Debe completar el campo descripción!');
    } else {
      axios.patch('http://localhost:5000/v1/api/idea/' + ideaID, {
        idea: idea,
        description: descripcion
      }, {
        headers: {
          'Authorization': token
        }
      })
        .then(dat => {
          if (dat.status !== 200 && dat.status !== 201) {
            document.cookie = "token=; max-age=0";
            return navigate('/');
          } else {
            setExito('Se editó exitosamente! ')
          }

        })
        .catch(() => {
          document.cookie = "token=; max-age=0";
          return navigate('/');
        })

    }


  }
  const crearIdea = (e) => {
    e.preventDefault();
    if (idea.trim() === '') {
      setError('Debe completar el campo idea!');
    } else if (descripcion.trim() === '') {
      setError('Debe completar el campo descripción!');
    } else {
      axios.post('http://localhost:5000/v1/api/idea', {
        idea: idea,
        description: descripcion,
      }, {
        headers: {
          'Authorization': token
        }
      }).then(
        (dat) => {
          if (dat.status !== 200 && dat.status !== 201) {
            document.cookie = "token=; max-age=0";
            return navigate('/');
          } else {
            setExito('Se creó exitosamente! ')
          }

        }
      )
        .catch(() => {
          document.cookie = "token=; max-age=0";
          return navigate('/');
        })
    }
  }

  return (
    <div className="container mt-5">
      <h1>{ideaID ? ('Editar idea') : ('Crear nueva idea')}</h1>
      <Form onSubmit={ideaID ? (editar) : (crearIdea)}>
        {error && exito === '' && <Alert className='mt-3' variant='danger'> {error}</Alert>}
        {!error && exito !== '' && <Alert className='mt-3' variant='success'> {exito}</Alert>}


        <Form.Group className="mb-3" >
          <Form.Label>Titulo de la idea</Form.Label>
          <Form.Control type="text" name="idea" value={idea} onChange={(e) => setIdea(e.target.value)} />

        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Descripcion de la idea</Form.Label>
          <Form.Control type="text" name="descripcion" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} />
        </Form.Group>

        <Button variant="outline-dark" type="submit">
          Submit
        </Button>
      </Form>
    </div>
  )
}

export default CrearEditarForm
