import React, {useEffect} from 'react';

import {useIdea,IdeaProvider} from '../../context/idea.context'
import Idea from '../../componentes/idea/Idea';

export default () => <IdeaProvider>
  <TopIdeas></TopIdeas>
</IdeaProvider>
const TopIdeas = () => {
    const {  getIdeas,
        ideas} = useIdea();
        //const [topsIdeas,setTopsIdeas] = useState();

        useEffect(()=>{
          getIdeas();
          console.log(ideas);
        },[])
        let votes = ideas.filter(vote=>{return vote.upvotes.length - vote.downvotes.length>10}).map(vote => {
            return vote.upvotes.length - vote.downvotes.length;
        });
        let filtrar = ideas.sort((a,b)=>{
            const votosA = a.upvotes.length - a.downvotes.length;
            const votosB = b.upvotes.length - b.downvotes.length;




            return votosA - votosB;
        })

        console.log(filtrar);
  return (
    <div className='container'>
        
<h1>Top ideas</h1>

{
        ideas.length > 0 &&
        ideas.map((idea) => <a href={`/idea/${idea._id}`} key={idea._id} style={{textDecoration: 'none'}}><Idea datos={idea} key={idea._id} /> </a>)
      }

    </div>
  )
}
