import React, { useState } from 'react'
import { Button } from 'react-bootstrap';
import AlertaEliminar from '../AlertaEliminar';

const BtnEliminar = (props) => {
    const config = {
        boton: 'danger',
        textoBtn: 'Eliminar'
    }
    const [modalShow, setModalShow] = useState(false);

    return (
        <>

           
                <Button variant={config.boton} onClick={() => setModalShow(true)}>
{config.textoBtn}     </Button>

                <AlertaEliminar
                    show={modalShow}
                    ideaID={props.ideaID}
                    title='¿Estás seguro que deseas eliminar esta idea?'  
                    onHide={() => setModalShow(false)}
                />


        </>);
}

export default BtnEliminar;
