import React, { useState } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';
import {useIdea} from '../../context/idea.context'

const Idea = ({ datos }) => {
  const  {getIdeas,ideas} = useIdea();
  const [idea, setIdea] = useState({
    id: datos._id,
    titulo: datos.idea,
    descripcion: datos.description,
    autor: datos.author.name,
    votos: datos.upvotes.length - datos.downvotes.length


  });
  console.log(idea);
  function cantVotos(datosDeVotos) {
    return datosDeVotos.upvotes.length - datosDeVotos.downvotes.length;
  }
  const downVote = e => {
    e.preventDefault();
    console.log('Entró');
    axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/downvote`)
      .then(dat => {
        datos = ideas;
getIdeas();
        setIdea({...idea,votos: cantVotos(dat.data) })

        console.log(`upvotes: ${dat.data.upvotes.length}   downvotes: ${dat.data.downvotes.length}`);
        console.log(`total: ${cantVotos(dat.data)}`);

      })
      .catch(console.log)

  }

  const upVote = e => {
    e.preventDefault();
    axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/upvote`)
      .then(dat => {
        datos = ideas;
        setIdea({...idea,votos: cantVotos(dat.data) })
        getIdeas();
        console.log(`upvotes: ${dat.data.upvotes.length}   downvotes: ${dat.data.downvotes.length}`);
        console.log(`total: ${cantVotos(dat.data)}`);

      })
      .catch(console.log)

  }


  return (

    <>

      <Card
        bg={'light'}
        key={idea.id}
        text={'dark'}
        className="mb-2 mt-2"
      >
        <Card.Header>{idea.titulo}</Card.Header>
        <Card.Body>
          <Card.Text>
            {idea.descripcion}
          </Card.Text>
          <footer className="blockquote-footer">
            Author: <cite title="Source Title">{idea.autor}</cite>
          </footer>
        </Card.Body>
        <Card.Footer>
          <h2>Puntos: {idea.votos }</h2>
          <form action={`http://localhost:5000/v1/api/idea/${datos._id}/upvote`} onSubmit={upVote}>
            <input type="hidden" name="up" value={datos._id} />
            <Button type="submit">positivo</Button>
          </form>
          <form action={`http://localhost:5000/v1/api/idea/${datos._id}/downvote`}  onSubmit={downVote}>

            <input type="hidden" name="down" value={datos._id} />

            <Button className="btn-danger" type="submit">negativo</Button>
          </form>



        </Card.Footer>
      </Card>


    </>

  )
}

export default Idea
