import React, { useState } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';

const Idea = ({ datos }) => {
  const [votos, setVotos] = useState(datos.upvotes.length - datos.downvotes.length);
  console.log(datos);
  function cantVotos(datosDeVotos) {
    return datosDeVotos.upvotes.length - datosDeVotos.downvotes.length;
  }
  const downVote = e => {
    e.preventDefault();
      console.log(datos._id);
    
      axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/downvote`)
        .then(dat => setVotos(cantVotos(dat.data)))
        .catch(console.log)
  
  }

  const upVote = e => {
    e.preventDefault();
      axios.post(`http://localhost:5000/v1/api/idea/${datos._id}/upvote`)
      .then(dat => setVotos(cantVotos(dat.data)))
      .catch(console.log)
 
  }


  return (

    <>

      <Card
        bg={'light'}
        key={datos._id}
        text={'dark'}
        className="mb-2 mt-2"
      >
        <Card.Header>{datos.idea}</Card.Header>
        <Card.Body>
          <Card.Text>
            {datos.description}
          </Card.Text>
          <footer className="blockquote-footer">
            Author: <cite title="Source Title">{datos.author.name}</cite>
          </footer>
        </Card.Body>
        <Card.Footer>
          <h2>Puntos: {votos}</h2>
          <form onSubmit={upVote}>
            <input type="hidden" name="up"  value={datos._id}/>
            <Button type="submit">positivo</Button>
          </form>
          <form onSubmit={downVote}>

          <input type="hidden" name="down"  value={datos._id}/>

            <Button className="btn-danger" type="submit">negativo</Button>
          </form>



        </Card.Footer>
      </Card>


    </>

  )
}

export default Idea
