import React, { useState, useMemo } from "react";
import axios from "axios";
const IdeaContext = React.createContext();

export function IdeaProvider(props) {
  const [ideas, setIdeas] = useState([]);
  function getIdeas() {
    axios.get('http://localhost:5000/v1/api/idea')
      .then(dat => {
        setIdeas(dat.data);
      })
      .catch(console.log)
  }  
   
  const value = useMemo(() => {
    return ({
      getIdeas,
   
      setIdeas,
      ideas })
  },[ideas])


  
return <IdeaContext.Provider value={value} {...props}/>
}

export function useIdea(){
  const context = React.useContext(IdeaContext);
  if(!context){
    throw new Error('useIdea debe estar adentro del provider');
  }
  return context;
}
