import React, {useState,useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import Loading from '../loading/Loading';
const AlertaEliminar = (props) => {
  const [loading, setLoading] = useState(false);
  const [titulo, setTitulo] = useState('¿Estás seguro que deseas la idea?');


  if(loading === true){

  setTimeout(()=>{
    setTitulo('Eliminado! Redirigiendo a tus ideas...');
    setTimeout(()=>{
      window.location = '/misideas';
    },2000)
  },3000)

  }
    useEffect(() => {
        axios.get(`http://localhost:5000/v1/api/idea/${props.ideaID}`)
          .then((dat) => {
            const userLocalStorage = localStorage.getItem('user').replace(/[ '"]+/g, '').trim();
    
            if (dat.data.author._id !== userLocalStorage) {
              return window.location = '/';
            }})
          .catch(console.log)
      }, [])
    
    
    
   
      const deleteIdea = () => {
        console.log(props.ideaID);

        const token = document.cookie.replace('token=', '');
        axios.delete('http://localhost:5000/v1/api/idea/' + props.ideaID,
          {
            headers: {
              'Authorization': token
            }
          })
          .then((data) => {
            setLoading(true);
            setTitulo('Eliminando...');

           })
          .catch((err)=>{
            console.log(err)
          })
      }
    
    return (
        <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
     
        centered
    >
        <Modal.Header closeButton className='bg-light'>
            <Modal.Title id="contained-modal-title-vcenter">
              {titulo}
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
        {loading === true ? <Loading/>: 'Esta acción es irreversible'}

         
        </Modal.Body>
        <Modal.Footer>
            <Button onClick={props.onHide} className='btn-secondary'>Close</Button>
            <Button variant="danger"  onClick={deleteIdea} >
        Eliminar
      </Button>
        </Modal.Footer>
    </Modal>
    );
}

export default AlertaEliminar;
