import React,{useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const MyAccount = () => {
    const token = document.cookie.replace('token=', '');
    const user = JSON.parse(localStorage.getItem('user'));
    const [userData, setUserData] = useState('');
    const [userStats, setUserStats] = useState();
    const navigate = useNavigate();
    useEffect(()=>{
        if (!token || !user) {
            navigate('/');
          } 
    },[]);
    useEffect(()=>{
        axios.get(`http://localhost:5000/v1/api/user/${user}`)
        .then((response)=>{
            const data = {
                username: response.data.username,
                name: response.data.name
            }
            setUserData(data);
            })
        .catch(console.log)
    },[]);
    useEffect(()=>{
        axios.get(`http://localhost:5000/v1/api/idea`)
        .then((response)=>{
          // const ideasTotal = response.data.filter(idea => {return idea.author._id === user}).length;
          let positiveVotes = 0;
          let negativeVotes = 0;

            response.data.forEach((el)=>{
                const positive = el.vote.filter((el)=>{return el.author._id === user && el.vote === true}).length;
                const negative = el.vote.filter((el)=>{return el.author._id === user && el.vote === false}).length;
                positiveVotes+= positive;
                negativeVotes+= negative;
                });
           console.log('votos positivos: '+positiveVotes);
            })
        .catch(console.log)
    },[userData !== '']);

  return (
    <div>
        {
            userData?
            <div> 
            <h1>Username: {userData.username}</h1>
            <h2>name: {userData.name}</h2>
            </div>
            :
            <h1>No data</h1>
        }


    </div>
  )
}

export default MyAccount
