import React,{useState} from 'react';
import {Form,Button} from 'react-bootstrap';

const CrearEditarForm = () => {
const [datos,setDatos] = useState({
    idea: '',
    descripcion: ''
})
    const editar = (e)=>{
        e.preventDefault();
        setDatos({idea: e.target.idea.value,
        descripcion: e.target.descripcion.value});       

    }
  return (
    <Form  onSubmit={editar}>
    <Form.Group className="mb-3" controlId="formBasicEmail">
      <Form.Label>Titulo de la idea</Form.Label>
      <Form.Control type="text" name="idea" id='idea' value={idea} onChange={(e) => setDatos({idea: e.target.value})}/>
      <Form.Text className="text-muted">
        We'll never share your email with anyone else.
      </Form.Text>
    </Form.Group>

    <Form.Group className="mb-3" controlId="formBasicPassword">
      <Form.Label>Descripcion de la idea</Form.Label>
      <Form.Control type="text" name="descripcion" id='descripcion' value={descripcion} onChange={(e) => setDatos({descripcion: e.target.value})}/>
    </Form.Group>
    <Form.Group className="mb-3" controlId="formBasicCheckbox">
      <Form.Check type="checkbox" label="Check me out" />
    </Form.Group>
    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form>  )
}

export default CrearEditarForm
