import React, { useState } from 'react';
import './style.css'
import axios from 'axios';
import { Card } from 'react-bootstrap';
import { Link} from 'react-router-dom';
import BtnSesion from '../alertas/btn_alertas/BtnSesion';

const Idea = ({ datos }) => {
  const token = document.cookie.replace('token=', '');
  const userLogged = JSON.parse(localStorage.getItem('user'));

  const [idea, setIdea] = useState({
    id: datos._id,
    titulo: datos.idea,
    descripcion: datos.description,
    autor: datos.author.name,
    votos: datos.vote.length,
    comentarios: datos.comments.length
  });
  const [votado,setVotado] = useState('');

 const votosnashe = datos.vote;
 if(votosnashe.length>0){

  for(let i = 0; i < votosnashe.length ; i++){
if(votosnashe[i].author._id === userLogged){
  if(votosnashe[i].vote === true){
    setVotado(true);
  }else{
    setVotado(false);
  }

}  }
 }


  function cantVotos(datosDeVotos) {
    return datosDeVotos;
  }
  const downVote = e => {
    e.preventDefault();
    if(votosnashe.length>0){

      for(let i = 0; i < votosnashe.length ; i++){
    if(votosnashe[i].author._id === userLogged){
      if(votosnashe[i].vote === true){

      }
     return alert('El usuario registrado ya a votado la idea')
    
    }  }
     }
    
    axios.post(`http://localhost:5000/v1/api/vote/${datos._id}`,{
      vote: false
    }, {
      headers: {
        'authorization': token
      }
    })
      .then(dat => {
        
        setIdea({ ...idea, votos: cantVotos(dat.data) })
      })
      .catch(console.log)
  }

  const upVote = e => {
    e.preventDefault();
    if(votosnashe.length>0){

      for(let i = 0; i < votosnashe.length ; i++){
    if(votosnashe[i].author._id === userLogged){
     return alert('El usuario registrado ya a votado la idea')
    
    }  }
     }
    
    axios.post(`http://localhost:5000/v1/api/vote/${datos._id}`
    ,{
      vote: true
    }, {
      headers: {
        'authorization': token
      }}
    )
      .then(dat => {
        setIdea({ ...idea, votos: cantVotos(dat.data) })
      })
      .catch(console.log)

  }


  return (
    <>
      <Card
        bg={'transparent'}
        key={idea.id}
        text={'dark'}
        className=" mt-5 shadow"
      >
        <Card.Header className='fw-bold'>{idea.titulo}</Card.Header>
        <Card.Body>
          <Card.Text>
            {idea.descripcion}
          </Card.Text>
          <footer className="blockquote-footer">
            Author: <cite title="Source Title">{idea.autor}</cite>
          </footer>
         
        </Card.Body>
        <Card.Footer >
          <div className='d-flex justify-content-around'>
          <h2>Puntos: {datos.vote.length}</h2>
          {token?
         <form onSubmit={upVote}>
         <input type="hidden" name="up" value={datos._id} />
     
           <button className={votado !== true? 'upvote' : 'voteup' }type="submit"></button>
                


       </form>:
          <form>
          <BtnSesion tipo='upvote'></BtnSesion>
          </form>
        
        
        }
          {token?
            <form onSubmit={downVote}>
            <input type="hidden" name="down" value={datos._id} />
            <button className='downvote'type="submit"></button>
          </form>:
          <form>
          <BtnSesion tipo='downvote'></BtnSesion>
          </form>
        
        
        }
        
          </div>
          <hr />
     <Link to={`/idea/${idea.id}`}className='text-center  text-decoration-none'style={{color:'#2b86c5'}} ><h3 >Comentarios{`(${idea.comentarios})`}</h3></Link>
        </Card.Footer>
      </Card>
    </>

  )
}

export default Idea
