import React, { useState, useMemo } from "react";
import axios from "axios";
const IdeaContext = React.createContext();

export function IdeaProvider(props) {
  const [ideas, setIdeas] = useState([]);
  const [ideaIndividual, setIdeaIndividual] = useState('');
  function getIdeas() {
    console.log('Se ejecutó');
    const token = document.cookie.replace('token=', '');
    axios.get('http://localhost:5000/v1/api/idea', {
      headers: {
        'authorization': token
      }

    })
      .then(dat => {
        setIdeas(dat.data);
        console.log(dat.data)

      })
      .catch(console.log)
  }  
  function getIdea(id){
    axios.get('http://localhost:5000/v1/api/idea/'+id)
      .then(dat => {
       setIdeaIndividual(dat.data);
      })
      .catch(console.log)

  } 
  const value = useMemo(() => {
    return ({
      getIdeas,
      getIdea,
      setIdeas,
      ideas,ideaIndividual })
  },[ideas])


  
return <IdeaContext.Provider value={value} {...props}/>
}

export function useIdea(){
  const context = React.useContext(IdeaContext);
  if(!context){
    throw new Error('useIdea debe estar adentro del provider');
  }
  return context;
}
