import React, { useState, useMemo } from "react";
import axios from "axios";
const IdeaContext = React.createContext();

export function IdeaProvider(props) {
  const [ideas, setIdeas] = useState([]);
  function getIdeas() {
    const token = document.cookie.replace('token=', '');
    axios.get('http://localhost:5000/v1/api/idea', {
      headers: {
        'authorization': token

      }

    })
      .then(dat => {
        setIdeas(dat.data);

      })
      .catch(console.log)
  }


  const [votos, setVotos] = useState(ideas.upvotes.length - ideas.downvotes.length);

  function cantVotos(datosDeVotos) {
    return datosDeVotos.upvotes.length - datosDeVotos.downvotes.length;
  }

  const downVote = e => {
    e.preventDefault();
    console.log('Entr贸');
    axios.post(`http://localhost:5000/v1/api/idea/${ideas._id}/downvote`)
      .then(dat => {
        setVotos(cantVotos(dat.data));
        console.log(dat.data);
      })
      .catch(console.log)
  }
 
  const upVote = e => {
    e.preventDefault();
    console.log('Entr贸');

    axios.post(`http://localhost:5000/v1/api/idea/${ideas._id}/upvote`)
      .then(dat => {
        setVotos(cantVotos(dat.data));

        console.log(dat.data);
      })
      .catch(console.log)

  }
  const value = useMemo(() => {
    return ({
      votos,
      getIdeas,
      ideas,
      upVote,
      downVote

    })
  },[votos,ideas])
return <IdeaContext.Provider value={value} {...props}/>
}

export function useIdea(){
  const context = React.useContext(IdeaContext);
  if(!context){
    throw new Error('useIdea debe estar adentro del provider');
  }
  return context;
}
