import React, { useState } from 'react';
import { Container, Alert } from 'react-bootstrap';
import './form.css';
import axios from 'axios';




const Login = () => {

  if (document.cookie.length > 0) {
    window.location = "/"
  }

  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const [error, setError] = useState("");
  const handleSubmit = e => {
    e.preventDefault();
    console.log('entr칩');
    if (!user) {
      setError('usuario');
      setTimeout(() => {
        setError('');
      }, 3000)
      return;
    }
    if (!pass) {
      setError('contrase침a');

      setTimeout(() => {
        setError('');
      }, 3000)


      return;
    }

    axios.post('http://localhost:5000/v1/api/auth/signin', {
      username: user,
      password: pass
    })
      .then(response => {
        document.cookie = `token=${response.data.token}; max-age=${60 * 300}; path=/; samesite=strict`;
        window.location = "/";
        setError("");

      })
      .catch(err => {
        setError('No se pudo ingresar intentelo mas tarde');
        setTimeout(() => {
          setError("");
        }, 3000)
        console.log(err);
      });




  }


  return (
    <Container>
      <div className="login">
        <div className="login-triangle" />
        <h2 className="login-header bg-dark">Log in</h2>
        <form className="login-container" onSubmit={handleSubmit} >
          <input className='mb-3' type="text" name="username" value={user} onChange={(e) => setUser(e.target.value)} placeholder="user" />
          <input className='mb-3' type="password" name="password" value={pass} onChange={(e) => setPass(e.target.value)} placeholder="Password" />
          <input type="submit" value="Ingresar" />
        </form>
        {error &&

          <Alert className='mt-3' variant='danger'>Debe completar el campo {error}</Alert>



        }

      </div>

    </Container>
  )
}

export default Login
